import React, { useState, useEffect } from "react";
import Link from "next/link";
import styled from "styled-components";
import academyImage from "../img/twit1.jpeg";
import { Menu, Col, Row, Input } from "antd";

const ImageBox = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
`;

function AppLayout({ children }) {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    return (
        <>
            <ImageBox
                src={academyImage}
                alt="academyIcon"
                width="120"
                height="100"
            />
            <Menu mode="horizontal">
                <Menu.Item>
                    <Link href="/">
                        <a>홈</a>
                    </Link>
                </Menu.Item>
                <Menu.Item>
                    <Link href="/signup">
                        <a>회원가입</a>
                    </Link>
                </Menu.Item>
                <Menu.Item>
                    <Link href="/studentregister">
                        <a>학생등록</a>
                    </Link>
                </Menu.Item>
                <Menu.Item>
                    <Input.Search
                        enterButton
                        style={{ verticalAlign: "middle" }}
                    ></Input.Search>
                </Menu.Item>
            </Menu>
            <Row gutter={8}>
                <Col xs={24} md={6}>
                    left
                </Col>
                <Col xs={24} md={12}>
                    {children}
                </Col>
                <Col xs={24} md={6}>
                    {isLoggedIn ? <div>login</div> : <div>no-login</div>}
                </Col>
            </Row>
        </>
    );
}

export default AppLayout;
